import { Line, LineChart } from 'recharts';
import { RechartsDevtools } from '@recharts/devtools';


export default function StaticDimensionChart() {
  return (
    <LineChart
      width={200}
      height={200}
      data={[
        { x: 1, y: 1 },
        { x: 2, y: 4 },
      ]}
    >
      <Line dataKey="y" />
      <RechartsDevtools />
    </LineChart>
  );
}

You can also use percents instead of pixels:

