function App() {
  const toggleFillColor = (fill) => {
    if (fill === "black") {
      return null;
    } else {
      return {
        style: {
          fill: "black",
        },
      };
    }
  };

  return (
    <VictoryChart
      theme={VictoryTheme.clean}
    >
      <VictoryArea
        data={sampleData}
        labels={({ datum }) => datum.y}
        events={[
          {
            target: "data",
            eventHandlers: {
              onClick: () => {
                return [
                  {
                    eventKey: "all",
                    mutation: (props) =>
                      toggleFillColor(
                        props.style
                          ?.fill,
                      ),
                  },
                ];
              },
            },
          },
        ]}
      />
    </VictoryChart>
  );
}

render(<App />);

