function App() {
  const toggleFillColor = (fill) => {
    if (fill === "black") {
      return null;
    } else {
      return {
        style: {
          fill: "black",
        },
      };
    }
  };

  return (
    <VictoryChart
      domainPadding={{ x: 20 }}
      theme={VictoryTheme.clean}
    >
      <VictoryBar
        data={sampleData}
        labels={({ datum }) => datum.y}
        events={[
          {
            target: "data",
            eventHandlers: {
              onClick: () => {
                return [
                  {
                    target: "data",
                    mutation: (props) =>
                      toggleFillColor(
                        props.style
                          ?.fill,
                      ),
                  },
                ];
              },
            },
          },
        ]}
      />
    </VictoryChart>
  );
}

