function App() {
  const [data, setData] =
    React.useState(getData());

  React.useState(() => {
    const setStateInterval =
      window.setInterval(() => {
        setData(getData());
      }, 4000);

    return () => {
      window.clearInterval(
        setStateInterval,
      );
    };
  }, []);

  return (
    <VictoryChart
      domainPadding={{ x: 25 }}
      theme={VictoryTheme.clean}
    >
      <VictoryCandlestick
        animate={{ duration: 1000 }}
        data={data}
      />
    </VictoryChart>
  );
}

function getData() {
  return _.range(5).map((i) => {
    return {
      x: `3/${i + 1}/23`,
      open: _.random(5, 10),
      close: _.random(10, 20),
      high: _.random(20, 25),
      low: _.random(1, 5),
    };
  });
}

render(<App />);

