const startDate = new Date(
  "2020-01-01T00:00:00.000Z",
);
const endDate = new Date(
  "2020-12-31T11:59:59.000Z",
);
const genres = [
  "pop",
  "rap",
  "hip-hop",
  "r&b",
  "indie",
  "alternative",
];

const listeningData = [];
for (let i = 0; i < 100; i++) {
  listeningData.push({
    day: new Date(
      _.random(
        startDate.getTime(),
        endDate.getTime(),
      ),
    ),
    genre:
      genres[
        _.random(0, genres.length - 1)
      ],
  });
}

const groupedData = _.groupBy(
  listeningData,
  ({ genre }) => genre,
);

const sharedAxisStyles = {
  tickLabels: {
    fontSize: 13,
  },
  axisLabel: {
    padding: 39,
    fontSize: 13,
    fontStyle: "italic",
  },
};

const App = () => {
  return (
    <VictoryChart
      height={450}
      padding={70}
      scale={{ x: "time" }}
      containerComponent={
        <VictoryVoronoiContainer
          style={{}}
          labels={({ datum }) =>
            datum.y > 0
              ? `${datum.y} ${datum.binnedData[0].genre} songs`
              : null
          }
        />
      }
      theme={VictoryTheme.clean}
    >
      <VictoryLabel
        x={225}
        y={25}
        textAnchor="middle"
        text="Songs listened to in 2020"
      />

      <VictoryStack
        colorScale={[
          "#003f5c",
          "#2f4b7c",
          "#665191",
          "#a05195",
          "#d45087",
          "#f95d6a",
          "#ff7c43",
          "#ffa600",
        ]}
      >
        {Object.entries(
          groupedData,
        ).map(([key, dataGroup]) => {
          return (
            <VictoryHistogram
              data={dataGroup}
              key={key}
              x="day"
              binSpacing={8}
              style={{
                data: {
                  strokeWidth: 0,
                },
              }}
            />
          );
        })}
      </VictoryStack>

      <VictoryAxis
        tickCount={12}
        tickFormat={(date) =>
          date.toLocaleString(
            "default",
            { month: "short" },
          )
        }
        style={sharedAxisStyles}
      />

      <VictoryAxis
        dependentAxis
        label="Total # of Songs"
        style={sharedAxisStyles}
      />
    </VictoryChart>
  );
};

render(<App />);

