function App() {
  const [data, setData] =
    React.useState(getData());
  const [bins, setBins] =
    React.useState(getBins());

  React.useState(() => {
    const setStateInterval =
      window.setInterval(() => {
        setData(getData());
        setBins(getBins());
      }, 4000);

    return () => {
      window.clearInterval(
        setStateInterval,
      );
    };
  }, []);

  return (
    <VictoryChart
      theme={VictoryTheme.clean}
      domainPadding={{ x: 20 }}
    >
      <VictoryHistogram
        animate={{ duration: 1000 }}
        bins={bins}
        data={data}
      />
    </VictoryChart>
  );
}

function getData() {
  return _.range(100).map(() => ({
    x: _.random(0, 300),
  }));
}

function getBins() {
  return _.range(
    0,
    _.random(100, 300),
    10,
  );
}

render(<App />);

