<VictoryBar
  theme={VictoryTheme.clean}
  labels={["a", "b", "c", "d", "e"]}
  data={[
    { x: 1, y: 1 },
    { x: 2, y: 2 },
    { x: 3, y: 3, label: "click me" },
    { x: 4, y: 2 },
    { x: 5, y: 1 },
  ]}
  events={[
    {
      target: "data",
      eventKey: [1, 2],
      eventHandlers: {
        onClick: (evt) => {
          evt.stopPropagation();
          return [
            {
              mutation: (props) => {
                return {
                  style: {
                    ...props.style,
                    fill: VictoryTheme
                      .clean.palette
                      ?.colors.teal,
                  },
                };
              },
            },
            {
              target: "labels",
              eventKey: 3,
              mutation: (props) => {
                return {
                  ...props.style,
                  text: "now click me",
                };
              },
            },
          ];
        },
      },
    },
    {
      target: "parent",
      eventHandlers: {
        onClick: () => {
          return [
            {
              target: "data",
              mutation: (props) => {
                return {
                  style: {
                    ...props.style,
                    fill: VictoryTheme
                      .clean.palette
                      ?.colors.red,
                  },
                };
              },
            },
          ];
        },
      },
    },
  ]}
/>

