LIVE EDITOR
const ScatterPoint = ({
  x,
  y,
  datum,
}) => {
  const [selected, setSelected] =
    React.useState(false);
  const [hovered, setHovered] =
    React.useState(false);

  return (
    <circle
      cx={x}
      cy={y}
      r={datum.x * datum.y}
      stroke={
        hovered ? "purple" : "white"
      }
      strokeWidth={2}
      fill={
        selected ? "cyan" : "magenta"
      }
      onClick={() =>
        setSelected(!selected)
      }
      onMouseEnter={() =>
        setHovered(true)
      }
      onMouseLeave={() =>
        setHovered(false)
      }
    />
  );
};

const App = () => {
  return (
    <VictoryChart
      theme={VictoryTheme.clean}
    >
      <VictoryScatter
        data={[
          { x: 1, y: 7 },
          { x: 2, y: 5 },
          { x: 3, y: 2 },
          { x: 4, y: 6 },
          { x: 5, y: 3 },
        ]}
        dataComponent={<ScatterPoint />}
      />
    </VictoryChart>
  );
};

render(<App />);

