function App() {
  return (
    <VictoryChart
      theme={VictoryTheme.clean}
      domain={{ y: [0, 100] }}
      containerComponent={
        <VictoryZoomContainer
          zoomDomain={{
            x: [5, 35],
            y: [0, 100],
          }}
        />
      }
    >
      <VictoryScatter
        data={getScatterData()}
        style={{
          data: {
            opacity: ({ datum }) =>
              datum.y % 5 === 0
                ? 1
                : 0.7,
            fill: ({ datum }) =>
              datum.y % 5 === 0
                ? "lightblue"
                : "lightgreen",
          },
        }}
      />
    </VictoryChart>
  );
}

function getScatterData() {
  return _.range(50).map((index) => {
    return {
      x: _.random(1, 50),
      y: _.random(10, 90),
      size: _.random(8) + 3,
    };
  });
}

render(<App />);

