  polar
  theme={VictoryTheme.clean}
  domain={{ x: [0, 360] }}
  innerRadius={50}
  events={[
    {
      childName: "all",
      target: "data",
      eventHandlers: {
        onMouseOver: (evt, props) => {
          return [
            {
              childName: "bar-2",
              mutation: () => {
                return {
                  style: Object.assign(
                    {},
                    props.style,
                    {
                      fill: VictoryTheme
                        .clean.palette
                        ?.colors.cyan,
